02076 
02077     public:
02078 
02079         pair< num_int , num_int > EuclidDiv (const num_int & arg) const {
02080             num_int & cthis = *this;
02081             const sign_e sgn_ndo = signo;
02082             const sign_e sgn_sor = arg.signo;
02083             num_int dvndo_int = cthis;
02084             dvndo_int.absp();
02085             dvndo_int.reduce();
02086             num_int dvsor_int = arg.abs();
02087             dvsor_int.reduce();
02088 
02089             num_int rem(dvndo_int);
02090 
02091             const usint cssor = dvsor_int.ceros_a_la_drcha(); //¿?
02092 
02093             num_int dvndo_fra(dvndo_int.substr(0,cssor));
02094             dvsor_int.erase(0,cssor);// division por una potencia de B
02095 
02096             dvsor_int.reduce();
02097             dvndo_int.reduce();
02098 
02099             dvndo_fra.reduce();
02100 
02101             const usint longtndo  = dvndo_int.size();
02102             const usint longtndo2 = dvndo_fra.size();
02103             const usint longtsor  = dvsor_int.size();
02104             const usint longsor   = longtsor;
02105 
02106             // hemos dividido por los ceros del divisor (por una potencia de B)
02107             // tanto divisor como dividendo, obteniendo dvndo_int y dvsor_int como
02108             // cocientes enteros.
02109             div_uno(rem,dvndo_int,dvsor_int,longtndo,longsor);
02110             // ahora hacemos lo mismo con el cociente de las potencias de B del
02111             // dividendo obteniendo dvndo_fra
02112 
02113             /* *
02114                 RemReal = RemObtenido*B^cssor + Dvndo_fra
02115              * */
02116             rem = rem.insert(0,cssor,0);
02117             rem += dvndo_fra;
02118 
02119             if (sgn_ndo != sgn_sor) dvndo_int.mC_B();
02120             // rehacer el resto tomando en cuenta los ceros quitados
02121             // acomodar el formato al signo de la operacion
02122             // determinar el signo de la operacion
02123             return make_pair(rem,dvndo_int);
02124         }
