02139 
02140             num_int dvndo_fra(dvndo_int.substr(0,cssor));
02141             dvsor_int.erase(0,cssor);// division por una potencia de B
02142 
02143             dvsor_int.reduce();
02144             dvndo_int.reduce();
02145 
02146             dvndo_fra.reduce();
02147 
02148             const usint longtndo  = dvndo_int.size();
02149             const usint longtndo2 = dvndo_fra.size();
02150             const usint longtsor  = dvsor_int.size();
02151             const usint longsor   = longtsor;
02152 
02153             // hemos dividido por los ceros del divisor (por una potencia de B)
02154             // tanto divisor como dividendo, obteniendo dvndo_int y dvsor_int como
02155             // cocientes enteros.
