02196         const num_int & operator ++ () {
02197             num_int & cthis = *this;
02198                         const size_t long_inic = cthis.size();
02199                         if (long_inic>1){
02200                                 cthis.reduce();}
02201                         if (cthis==num_int(vminus,dig(B-1),dig(B-1))){
02202                                 cthis.clear();
02203                                 cthis.resize(1);
02204                                 cthis[0]=dig(0);
02205                                 signo = vplus;
02206                                 cthis.reduce();
02207                                 return cthis;}
02208                         bool es_negativo = (signo==vminus);
02209                         signo = vplus;
02210                         aux=spardigs(vplus,0,1);
02211                         aux += spardigs(vplus,0,cthis[0]);
02212                         cthis[0] = aux.g_uds();
02213                         aux.p_uds(aux.g_decs());
02214                         aux.p_decs(0);
02215                         bool seguir = (aux.g_uds()!=dig(0));
02216                         ullint i=1;
02217                         while( seguir and (i<long_inic)) {
02218                                 aux += spardigs(vplus,0,cthis[i]);
02219                                 cthis[i]=(aux.g_uds());
02220                                 aux.p_uds(aux.g_decs());
02221                                 seguir = (aux.g_uds()!=dig(0));
02222                                 aux.p_decs(0);
02223                                 ++i;
02224                         }
02225                         if( seguir and (i >= long_inic) ) {
02226                                 cthis.resize(long_inic+1);
02227                                 cthis[i]=aux.g_uds();
02228                         }
02229                         ((seguir)and(es_negativo)?(signo=vplus):(signo=vminus));//
02230                         signo=(es_negativo?vminus:vplus);
02231                         cthis.reduce();
02232                         return cthis;
02233         }
