02274         const num_int & operator -- () {
02275             num_int & cthis = *this;
02276                         const size_t long_inic = cthis.size();
02277                         // BEGIN TRATAMIENTO DEL 0
02278                         if (cthis==num_int(vplus,dig(0),dig(0))){
02279                                 cthis.clear();
02280                                 cthis.resize(1);
02281                                 cthis[0]=dig(B-1);
02282                                 signo = vminus;
02283                                 return cthis;
02284                         }
02285                         bool es_negativo = (signo==vminus);
02286                         // END TRATAMIENTO DEL 0
02287                         // BEGIN TRATAMIENTO DE POTENCIA DE B NEGATIVAS
02288                         bool es_pot_de_B_neg = false;
02289                         if (es_negativo) {
02290                                 es_pot_de_B_neg = true;
02291                                 for(slint i=0 ; (i < long_inic) and es_pot_de_B_neg ; ++i)
02292                                 {
02293                                         es_pot_de_B_neg = es_pot_de_B_neg and (cthis[i]==dig(0));
02294                                 }
02295                         } // Hemos calculado si es potencia de 10 negativa
02296                         if (es_pot_de_B_neg) {
02297                                 cthis.resize(long_inic+1);
02298                                 for(slint i=0; i < long_inic; ++i)
02299                                         cthis[i]=dig(B-1);
02300                                 cthis[long_inic] = dig(B-2);
02301                                 signo = vminus;
02302                                 return cthis;
02303                         } // Vemos que -000 pasa a -8999
02304                         // END TRATAMIENTO DE POTENCIA DE B NEGATIVAS
02305                         signo = vplus;
02306                         aux=spardigs(vminus,dig(B-1),dig(B-1));
02307                         aux+= spardigs(vplus,0,cthis[0]);
02308                         cthis[0] = aux.g_uds();
02309                         aux.p_uds(aux.g_decs());
02310                         aux.p_decs(B-1);
02311                         bool seguir = (aux.g_uds()!=dig(0));
02312                         ullint i=1;
02313                         while( seguir and (i<long_inic)) {
02314                                 aux += spardigs(vplus,0,cthis[i]);
02315                                 cthis[i]=(aux.g_uds());
02316                                 aux.p_uds(aux.g_decs());
02317                                 seguir = (aux.g_uds()!=dig(0));
02318                                 aux.p_decs(B-1);
02319                                 ++i;
02320                         }
02321                         if( seguir and (i >= long_inic) ) {
02322                                 cthis.resize(long_inic+1);
02323                                 cthis[i]=aux.g_uds();
02324                         }
02325                         signo=(es_negativo?vminus:vplus);
02326                         cthis.reduce();
02327                         return cthis;
02328         }
