02343                         // END TRATAMIENTO DEL 0
02344                         // BEGIN TRATAMIENTO DE POTENCIA DE B NEGATIVAS
02345                         bool es_pot_de_B_neg = false;
02346                         if (es_negativo) {
02347                                 es_pot_de_B_neg = true;
02348                                 for(slint i=0 ; (i < long_inic) and es_pot_de_B_neg ; ++i)
02349                                 {
02350                                         es_pot_de_B_neg = es_pot_de_B_neg and (cthis[i]==dig(0));
02351                                 }
02352                         } // Hemos calculado si es potencia de 10 negativa
02353                         if (es_pot_de_B_neg) {
02354                                 cthis.resize(long_inic+1);
02355                                 for(slint i=0; i < long_inic; ++i)
02356                                         cthis[i]=dig(B-1);
02357                                 cthis[long_inic] = dig(B-2);
02358                                 signo = vminus;
02359                                 return cpThis;
02360                         } // Vemos que -000 pasa a -8999
02361                         // END TRATAMIENTO DE POTENCIA DE B NEGATIVAS
02362                         signo = vplus;
02363                         aux=spardigs(vminus,dig(B-1),dig(B-1));
02364                         aux+= spardigs(vplus,0,cthis[0]);
02365                         cthis[0] = aux.g_uds();
02366                         aux.p_uds(aux.g_decs());
02367                         aux.p_decs(B-1);
02368                         bool seguir = (aux.g_uds()!=dig(0));
02369                         ullint i=1;
02370                         while( seguir and (i<long_inic)) {
02371                                 aux += spardigs(vplus,0,cthis[i]);
02372                                 cthis[i]=(aux.g_uds());
02373                                 aux.p_uds(aux.g_decs());
02374                                 seguir = (aux.g_uds()!=dig(0));
02375                                 aux.p_decs(B-1);
02376                                 ++i;
02377                         }
02378                         if( seguir and (i >= long_inic) ) {
02379                                 cthis.resize(long_inic+1);
02380                                 cthis[i]=aux.g_uds();
02381                         }
02382                         signo=(es_negativo?vminus:vplus);
02383                         cthis.reduce();
02384                         return cpThis;
02385         }
02386 
02387                                                                         /******************************/
02388                                                                         /*                                                                      */
02389                                                                         /*         REDUCE                  */
02390                                                                         /*                                                              */
02391                                                                         /******************************/
02392 
02393         const num_int & reduce() {
02394             num_int & cthis = *this;
02395                         const usint nsz = cthis.digs_no_significativos();
02396                         const usint longitud = cthis.size();
02397                         if ((longitud==1)or(longitud==0)) return cthis;
02398                         const usint pos_p1 = longitud-nsz;
02399                         return cthis.erase(pos_p1,nsz);
02400         }
02401 
02402         num_int reduce() const {
02403             const num_int & cthis = *this;
02404             num_int cpyreducida(cthis);
02405             const usint nsz = cpyreducida.digs_no_significativos();
02406                         const usint longitud = cpyreducida.size();
02407             const usint pos = longitud-nsz;
02408                         return cpyreducida.erase(pos,nsz);
02409         }
02410 
02411                 const num_int & reduce_fracc() {
02412             num_int & cthis = *this;
02413             const usint nsz = cthis.digs_fracc_no_significativos()-1;
02414             const usint pos = 0;
02415             cthis.erase(pos,nsz);
02416             return cthis;
02417         }
02418 
02419         num_int reduce_fracc() const {
02420             num_int & cthis = *this;
02421             num_int cpyreducida(cthis);
02422             const usint nsz = cpyreducida.digs_no_significativos()-1;
02423             const usint pos = 0;
02424             cpyreducida.erase(pos,nsz);
02425             return cpyreducida;
02426         }
02427 
02428 
02429                                                                 /*********************************/
02430                                                                 /*                                                                          */
02431                                                                 /*         ISTREAM Y OSTREAM    */
02432                                                                 /*                                                                          */
02433                                                                 /*********************************/
02434 
02435         template<const uchint Base>
02436         friend
02437         istream & operator >> (istream & is,num_int<Base> & arg) {
02438             enum estado_e {e0,e1,e2,e3,e4,e5,e6,e7,e8,e9,e10,e11};
02439             vector<string> num(255);
02440             string num_sgn;
02441             string num_base;
02442             estado_e est_act = e0;
02443             uchint i=0;
02444             char c;
02445             do {
02446                 is >> c;
02447                 switch (est_act) {
02448                 case e0 :
02449                     if (c=='i') est_act = e1;
02450                     break;
02451                 case e1 :
02452                     if (c=='n') est_act = e2;
02453                     else est_act = e0;
02454                     break;
02455                 case e2 :
02456                     if (c=='t') est_act = e3;
02457                     else est_act = e0;
02458                     break;
02459                 case e3 :
02460                     if (c=='#') est_act = e4;
02461                     else est_act = e0;
02462                     break;
02463                 case e4 :
02464                     if ((c=='1')||(c=='0')||(c=='-')||(c=='+')) {
02465                         est_act = e5;
02466                         num_sgn.push_back(c-'0');
02467                     }
02468                     else est_act = e0;
02469                     break;
02470                 case e5 :
02471                     if (c==':') est_act = e6;
02472                     else est_act = e0;
02473                     break;
02474                 case e6 :
02475                     if (c==':') est_act = e7;
02476                     else est_act = e0;
02477                     break;
02478                 case e7 :
02479                     if ((c<='9')and(c>='0')) {
02480                         num[i].push_back(c-'0');
02481                     }
02482                     else if (c==':') {
02483                         ++i;
02484                         est_act = e6;
02485                     }
02486                     else if (c=='#') est_act = e8;
02487                     else est_act=e0;
02488                     break;
02489                 case e8 :
02490                     if (c == 'B') est_act = e9;
02491                     else est_act = e0;
02492                     break;
02493                 case e9 :
02494                     if ((c<='9')and(c>='0')) {
02495                         est_act = e10;
02496                         num_base.push_back(c-'0');
02497                     }
02498                     else est_act = e0;
02499                     break;
02500                 case e10 :
02501                     if ((c<='9')and(c>='0')) {
02502                         num_base.push_back(c-'0');
02503                     }
02504                     else est_act = e11;
02505                     break;
02506                 case e11 :
02507                     break;
02508                 default :
02509                     est_act = e0;
02510                 }
02511                 uint numero_base_recogido=0;
02512                 if (est_act==e11) {
02513                     for (uint k=num_base.size() ; k > 0 ; --k ) {
02514                         numero_base_recogido *= 10;
02515                         numero_base_recogido += num_base[k];
02516                     }
02517                     numero_base_recogido *= 10;
02518                     numero_base_recogido += num_base[0];
02519                     if (numero_base_recogido!=Base) est_act = e0;
02520                 }
02521             } while ((est_act!=e11)and(c!='\n'));
02522             basic_string< dig_t<Base> > digito;
02523             digito.resize(i+1);
02524             for (uchint j=0 ; j < 256 ; ++j) {
02525                 digito[j]=0;
02526                 for (uint k=num[j].size() ; k > 0 ; --k ) {
02527                     digito[j] *= 10;
02528                     digito[j] += (num[j][k]-'0');
02529                 }
02530                 digito[j] *= 10;
02531                 digito[j] += (num[j][0]-'0');
02532             }
02533             sign_e sgn;
02534             if ((num_sgn[0]=='1') || (num_sgn[0]=='-')) sgn = vminus;
02535             else sgn = vplus;
02536             arg.signo = sgn;
02537             arg.lst_digs = digito;
02538             arg.aux = spardigs_t<Base>();
02539             return is;
02540         }
02541 
02542         template<const uchint Base>
02543         friend
02544         ostream & operator << (ostream & os,num_int<Base> arg) {
02545             const uchint sz = arg.size();
02546             os << "int#"
02547             << ((arg.signo == vplus)?('+'):('-'));
02548             for (int k=sz-1 ; k>-1 ; --k) {
02549                 os << "::"
02550                    << static_cast<usint>(arg[k]());
02551             }
02552             os <<"#B"
02553             << static_cast<usint>(Base) ;
02554             return os;
02555         }
02556     };
