00194     {
00195                 map< string , par_dir_t >::iterator it = mapvd.begin();
00196                 const map< string , par_dir_t >::iterator itend = mapvd.end();
00197                 for( ; it != itend ; ++it) {
00198                         if (((*it).second.second == var_base)or((*it).first==nombre)) return false;
00199                 }
00200                 if (mapmem[base][var_base]==false) {
00201                         mapvd.insert(
00202                                 std::make_pair<std::string,par_dir_t>
00203                                 (
