00481 operator uint () const {
00482         const nat_num_t & cthis = (*this);
00483         const uint sz = (cthis.reduce()).size();
00484         const uint pos_max = sz-1;
00485         const uint nds = num_digs_max_base_B_en_Int_T<uint,B>();
00486         //cout << nds << endl;
00487         uchint ret = 0;
00488         for(uint il = 0; (il < sz)and(il < nds) ;++il) {
00489                 ret *= B;
00490                 ret += cthis[sz-nds+il].Dig2UInt();
00491         }
00492         return ret;
00493 }
