00566 usint ceros_a_la_izqda () const {
00567         const nat_num_t & cthis = (*this);
00568         //digitos no significativos para n√∫meros positivos
00569         bool Exit = false;
00570         int sz=0;
00571         const size_t fin= cthis.size();
00572         for ( uint i=0 ; (i < fin) and (!Exit) ; ++i) {
00573                 if (cthis[i]==0) ++sz;
00574                 else Exit=true;
00575         }
00576         return sz;
00577 }
00578 //digitos no significativos para n√∫meros positivos
00579 size_t ceros_a_la_drcha () const {
00580         const nat_num_t & cthis = (*this);
00581         bool Exit = false;
00582         size_t sz=0;
00583         const size_t fin=cthis.size();
00584         const size_t finl = fin-1;
00585         if (cthis[finl]==0) {
00586                 for ( int i=finl ; (i >=0) and (!Exit) ; --i) {
00587                         if (cthis[i]==0) ++sz;
00588                         else Exit=true;
00589                 }
00590         }
00591         return sz;
00592 }
00593 
00594 usint digs_no_significativos () const {
00595         const nat_num_t & cthis = (*this);
00596         //digitos no significativos
00597         return ceros_a_la_izqda();
00598 }
