00611 bool operator == (const nat_num_t & arg) const {
00612         const nat_num_t & cthis = (*this);
00613         const usint thisnsz = ceros_a_la_izqda();
00614         const usint thisvsz = cthis.size()-thisnsz;
00615         const usint argnsz = arg.ceros_a_la_izqda();
00616         const usint argvsz = arg.size()-argnsz;
00617         if ((thisvsz > argvsz)||(thisvsz < argvsz))
00618                 return false;
00619         else {
00620                 for (slint k=(thisvsz-1);k>=0;--k)
00621                         if (arg[k]!=cthis[k]) return false;
00622                 return true;
00623         }
00624 }
00625 
00626 bool operator != (const nat_num_t & arg) const {
00627         const nat_num_t & cthis = (*this);
00628         const usint thisnsz = ceros_a_la_izqda();
00629         const usint thisvsz = (cthis.size())-thisnsz;
00630         const usint argnsz = arg.ceros_a_la_izqda();
00631         const usint argvsz = arg.size()-argnsz;
00632         if ((thisvsz > argvsz)||(thisvsz < argvsz))
00633                 return true;
00634         else {
00635                 for (slint k=(thisvsz-1);k>=0;--k)
00636                         if (arg[k]!=cthis[k])
00637                                 return true;
00638                 return false;
00639         }
00640 }
