00642 bool operator >= (const nat_num_t & arg) const {
00643         const nat_num_t & cthis = (*this);
00644         const usint thisnsz = ceros_a_la_izqda();
00645         const usint thisvsz = (cthis.size())-thisnsz;
00646         const usint argnsz = arg.ceros_a_la_izqda();
00647         const usint argvsz = arg.size()-argnsz;
00648 
00649         if (thisvsz > argvsz) return true;
00650         else if (thisvsz < argvsz) return false;
00651 
00652         for (usint k=0;k<thisvsz;++k) {
00653                 if ((cthis[k] > arg[k])) return true;
00654                 else if ((cthis[k] < arg[k])) return false;
00655         }
00656         return false;
00657 }
