00711 
00712 nat_num_t operator + (const nat_num_t & arg) const {
00713 
00714         const nat_num_t & cthis = (*this);
00715         pardigs_t<B> tempt ;
00716         const bool cthis_is_max = (cthis>=arg);
00717         nat_num_t<B> cpyarg(arg);
00718         nat_num_t<B> cpythis(cthis);
00719         cpythis.reduce();
00720         cpyarg.reduce();
00721         nat_num_t<B> * pmax = ((cthis_is_max)?(&cpythis):(&cpyarg));
00722         nat_num_t<B> & max = (*pmax);
00723         nat_num_t<B> * pmin = ((cthis_is_max)?(&cpyarg):(&cpythis));
00724         nat_num_t<B> & min = (*pmin);
00725 
00726         dig carry(0);
00727 
00728         int M = (max.size())-1;
00729         int m = (min.size())-1;
00730         for ( ; !(m<0) ; --M,--m) {
00731                 tempt.p_uds(max[M]);
00732                 tempt.p_decs(dig_t(0));
00733                 tempt += min[m];
00734                 tempt += carry;
00735                 carry =tempt.g_decs();
00736                 max[M]=tempt.g_uds();
00737         }
00738         for ( ; !(M<0) ; --M) {
00739                 tempt.p_uds(max[M]);
00740                 tempt.p_decs(dig_t(0));
00741                 tempt += carry;
00742                 carry =tempt.g_decs();
00743                 max[M]=tempt.g_uds();
00744         }
00745 
00746         if (carry!=dig(0)) {
00747                 max.push_front(carry);
00748         }
00749 
00750         max.aux =  pardigs();
00751         return max;
00752 }
00753 
00754 nat_num_t operator - (const nat_num_t & arg) const {
00755         nat_num_t ret(*this);
00756         ret.reduce();
00757         const size_t szret = ret.size();
00758         nat_num_t cpy(arg.minus(ret.size()));
00759         ret += cpy;
00760         if (ret.size() > szret) {
00761                 ret.erase(0,1);
00762         }
00763         ret.reduce();
00764         return ret;
00765 }
