00767 // Utilizamos una funcion nat_num_t * dig_t
00768 nat_num_t operator * (const nat_num_t & arg) const {
00769         const nat_num_t & cthis = (*this);
00770         cthis.reduce();
00771         nat_num_t multtemp;
00772         nat_num_t sumatemp;
00773         nat_num_t cadena_de_ceros;
00774         const int argsz = arg.size();
00775         const int argmsb = argsz-2;
00776 
00777 
00778         multtemp = cthis*arg[argsz-1];
00779         sumatemp += multtemp;
00780 
00781         for (int j = argmsb ; j >= 0 ; --j) {
00782                 multtemp = cthis*arg[j];
00783                 multtemp &= cadena_de_ceros;
00784                 sumatemp += multtemp;
00785                 cadena_de_ceros.push_back(dig(0));
00786         }
00787 
00788         return sumatemp;
00789 }
