00849 nat_num_t operator % (const nat_num_t & arg) const {
00850         const nat_num_t & cthis = (*this);
00851         nat_num_t dvndo_int(cthis); //Siempre ser√° positivo
00852         dvndo_int.reduce();
00853         nat_num_t rem(dvndo_int);
00854         rem.reduce();
00855         nat_num_t dvsor_int(arg);//Siempre ser√° positivo
00856         dvsor_int.reduce();
00857         dvndo_int/=dvsor_int;
00858         dvndo_int*=dvsor_int;
00859         rem -= dvndo_int;
00860         return rem;
00861 }
00862 
00863 nat_num_t operator * (pardigs a) const {
00864         const nat_num_t & cthis = (*this);
00865         pardigs                 temp;
00866         dig                     carry=0;
00867         nat_num_t               ret(*this);
00868         for ( ssint k=(cthis.size()-1) ; k >= 0  ; --k ) {
00869                 temp = (pardigs(cthis[k]))*(pardigs(a.g_first()));
00870                 temp += carry;
00871                 ret[k]= temp.g_first();
00872                 carry=temp.g_second();
00873         }
00874         if (carry != 0)
00875                 ret.insert(0,carry);
00876         ret.reduce();
00877         carry = 0;
00878         nat_num_t ret2(ret);
00879         for ( ssint k=((cthis.size())-1) ; k >= 0  ; --k ) {
00880                 temp = pardigs(cthis[k])*pardigs(a.g_second());
00881                 temp += carry;
00882                 ret2.erase(0,k-1);
00883                 ret2 += temp.g_first();//¬ø?
00884                 carry = temp.g_second();
00885         }
00886         ret += ret2;
00887         if (carry != 0)
00888                 ret.insert(0,carry);
00889         ret.reduce();
00890 
00891         return ret;
00892 }
