00894 //      nat_num_t operator / (pardigs) const;
00895 //      nat_num_t operator % (pardigs) const;
00896 
00897 nat_num_t operator + (dig arg) const {
00898         nat_num_t ret(*this);
00899         ret.reduce();
00900         pardigs tempt;
00901         const usint szt = ret.size();
00902         ssint k = szt-1;
00903         dig carry;
00904 
00905         tempt.p_uds(ret[k]);
00906         tempt += arg;
00907         ret[k] = tempt.g_uds();
00908         carry = tempt.g_decs();
00909         tempt.p_decs(dig(0));
00910 
00911         if (szt>=1) {
00912                 for ( k=szt-2 ; k>=0 ; --k) {
00913                         tempt.p_uds(ret[k]);
00914                         tempt += carry;
00915                         ret[k]=tempt.g_uds();
00916                         carry =tempt.g_decs();
00917                         tempt.p_decs(dig(0));
00918                 }
00919         }
00920 
00921         ret.push_front(carry) ;
00922         ret.aux = pardigs();
00923         ret.reduce();
00924         return ret;
00925 }
00926 
00927 nat_num_t operator - (dig arg) const {
00928         nat_num_t ret(*this);
00929         ret.reduce();
00930         if (arg == dig(0)) return ret;
00931         nat_num_t cpy(nat_num_t(arg).minus(ret.size()));
00932         pardigs tempt;
00933         pardigs tempa;
00934         sint szt = ret.size();
00935         sint k = szt-1;
00936         tempt.p_uds(ret[k]);
00937         tempt.p_decs(dig(0));
00938         tempa.p_uds(cpy[k]);
00939         tempa.p_decs(dig(0));
00940         tempt += tempa;
00941         ret[k] =tempt.g_uds();
00942         dig carry(tempt.g_decs());
00943         --k;
00944         for ( ; k >= 0 ; --k) {
00945                 tempt.p_uds(ret[k]);
00946                 tempt += cpy[k];
00947                 tempt += carry;
00948                 carry =tempt.g_decs();
00949                 ret[k] =tempt.g_uds();
00950         }
00951 
00952         ret.reduce();
00953         ret.aux =  pardigs();
00954         return ret;
00955 }
00956 
00957 nat_num_t operator * (dig a) const {
00958         const nat_num_t & cthis = (*this);
00959         pardigs         temp;
00960         dig             carry=0;
00961         const int       sz = cthis.size();
00962         const int       lsb = sz - 1;
00963         nat_num_t ret(*this);
00964         for ( int k=lsb ; k >= 0 ; --k ) {
00965                 temp = pardigs(dig(0),cthis[k])*a;
00966                 temp += carry;
00967                 ret[k]= temp.g_uds();
00968                 carry=temp.g_decs();
00969         }
00970         if (carry != 0) {
00971                 ret.push_front(carry);
00972         }
00973         ret.reduce();
00974         return ret;
00975 }
