00984 const nat_num_t & operator += (const nat_num_t &arg) {
00985         nat_num_t & cthis = (*this);
00986         cthis.reduce();
00987         pardigs_t<B> tempt ;
00988         const bool cthis_is_max = (cthis>=arg);
00989         nat_num_t<B> cpyarg(arg);
00990         cpyarg.reduce();
00991         nat_num_t<B> * pmax = ((cthis_is_max)?(&cthis):(&cpyarg));
00992         nat_num_t<B> & max = (*pmax);
00993         nat_num_t<B> * pmin = ((cthis_is_max)?(&cpyarg):(&cthis));
00994         nat_num_t<B> & min = (*pmin);
00995 
00996         dig carry(0);
00997 
00998         int M = (max.size())-1;
00999         int m = (min.size())-1;
01000         cthis.aux = pardigs();
01001         for ( ; !(m<0) ; --M,--m) {
01002                 tempt.p_uds(max[M]);
01003                 tempt.p_decs(dig_t(0));
01004                 tempt += min[m];
01005                 tempt += carry;
01006                 carry =tempt.g_decs();
01007                 max[M]=tempt.g_uds();
01008         }
01009         for ( ; !(M<0) ; --M) {
01010                 tempt.p_uds(max[M]);
01011                 tempt.p_decs(dig_t(0));
01012                 tempt += carry;
01013                 carry =tempt.g_decs();
01014                 max[M]=tempt.g_uds();
01015         }
01016 
01017         if (carry!=dig(0)) {
01018                 max.push_front(carry);
01019         }
01020 
01021         if ((&cthis)!=(&max)) {
01022                 cthis = (max.reduce());
01023         }
01024         else {
01025                 cthis.reduce();
01026         }
01027         cthis.aux =  pardigs();
01028         return cthis;
01029 }
