01167         tempt += arg;
01168         cthis[k-1]=tempt.g_uds();
01169         carry = tempt.g_decs();
01170         --k;
01171         for (; k > 0 ; --k) {
01172                 tempt = pardigs();
01173                 tempt += carry;
01174                 tempt += cthis[k-1];
01175                 cthis[k-1]=tempt.g_uds();
01176                 carry = tempt.g_decs();
