01184 const nat_num_t & operator -= (dig arg) {
01185         nat_num_t & cthis = (*this);
01186         cthis.reduce();
01187         if (arg == dig(0)) return cthis;
01188         nat_num_t cpy(nat_num_t(arg).minus(cthis.size()));
01189         pardigs tempt;
01190         pardigs tempa;
01191         sint szt = cthis.size();
01192         sint k = szt-1;
01193         tempt.p_uds(cthis[k]);
01194         tempt.p_decs(dig(0));
01195         tempa.p_uds(cpy[k]);
01196         tempa.p_decs(dig(0));
01197         tempt += tempa;
01198         cthis[k] =tempt.g_uds();
01199         dig carry(tempt.g_decs());
01200         --k;
01201         for ( ; k >= 0 ; --k) {
01202                 tempt.p_uds(cthis[k]);
01203                 tempt += cpy[k];
01204                 tempt += carry;
01205                 carry =tempt.g_decs();
01206                 cthis[k] =tempt.g_uds();
01207         }
01208 
01209         cthis.reduce();
01210         cthis.aux =  pardigs();
01211         return cthis;
01212 }
01213 
01214 const nat_num_t & operator *= (dig arg) {
01215         nat_num_t & cthis = (*this);
01216         pardigs temp;
01217         dig     carry=0;
01218         cthis.reduce();
01219         for ( ssint k=((cthis.size())-1) ; k >= 0 ; --k ) {
01220                 temp.p_uds(cthis[k]);
01221                 temp *= arg;
01222                 temp += carry;
01223                 cthis[k]= temp.g_uds();
01224                 carry=temp.g_decs();
01225                 temp.p_decs(dig(0));
01226         }
01227         if (carry != 0) push_front(carry);
01228         cthis.reduce();
01229         return cthis;
01230 }
01231 
01232                                                 /****************************/
01233                                                 /*                                                      */
01234                                                 /*      COMPLEMENTO A BASE Y    */
01235                                                 /*              BASE MENOS 1            */
01236                                                 /*                                                      */
01237                                                 /****************************/
01238 
01239 nat_num_t operator - () const {
01240         nat_num_t cpy(*this);
01241         cpy.reduce();
01242         crstriterator start = cpy.rend();
01243         rstriterator  it    = cpy.rbegin();
01244         for ( ; it != start ; ++it ) {
01245                 (*it) = (!(*it));
01246         }
01247         cpy.reduce();
01248         it = cpy.rbegin();
01249         cpy.aux.p_uds(*it);
01250         cpy.aux.p_decs(dig());
01251         cpy.aux += pd_1<B>();
01252         (*it) = cpy.aux.g_uds();
01253         ++it;
01254         if ((cpy.aux.g_decs())!= dig(0)) {
01255                 for ( ; it != start ; ++it ) {
01256                         cpy.aux.p_uds(cpy.aux.g_decs());
01257                         cpy.aux.p_decs(0);
01258 
01259                         if (cpy.aux == pardigs()) break;
01260                         cpy.aux += pardigs(*it);
01261                         (*it) = cpy.aux.g_uds();
01262                 }
01263                 cpy.push_front(cpy.aux.g_uds());
01264                 cpy.push_front(cpy.aux.g_decs());
01265         }
01266 
01267         cpy.aux = pardigs();
01268         cpy.reduce();
01269         return cpy;
01270 }
01271 
01272 
01273 nat_num_t minus (size_t sz) const {
01274         nat_num_t cpy(*this);
01275         cpy.reduce();
01276         if (cpy.size() < sz) {
01277                 const int tm = sz - cpy.size();
01278                 for(int i=0; i<tm ; ++i) {
01279                         cpy.push_front(dig(0));
01280                 }
01281         }
01282         else if (cpy.size() > sz) {
01283                 cpy.erase(0,1);
01284         }
01285         crstriterator start = cpy.rend();
01286         rstriterator  it    = cpy.rbegin();
01287         for ( ; it != start ; ++it ) {
01288                 (*it) = (!(*it));
01289         }
01290         it = cpy.rbegin();
01291         cpy.aux.p_uds(*it);
01292         cpy.aux.p_decs(dig(0));
01293         cpy.aux += pd_1<B>();
01294         (*it) = cpy.aux.g_uds();
01295         ++it;
01296         if ((cpy.aux.g_decs())!= dig(0)) {
01297                 for ( ; it != start ; ++it ) {
01298                         cpy.aux.p_uds(cpy.aux.g_decs());
01299                         cpy.aux.p_decs(0);
01300 
01301                         if (cpy.aux == pardigs()) break;
01302                         cpy.aux += pardigs(*it);
01303                         (*it) = cpy.aux.g_uds();
01304                 }
01305                 cpy.push_front(cpy.aux.g_uds());
01306                 cpy.push_front(cpy.aux.g_decs());
01307         }
01308 
01309         cpy.aux = pardigs();
01310         if (cpy.size() < sz) {
01311                 const int tm = sz - cpy.size();
01312                 for(int i=0; i<tm ; ++i) {
01313                         cpy.push_front(dig(B-1));
01314                 }
01315         }
01316         else if (cpy.size() > sz) {
01317                 cpy.erase(0,1);
01318         }
01319         //cpy.reduce();
01320         return cpy;
01321 }
01322 
