01325         nat_num_t cpy(*this);
01326         const striterator fin = cpy.end();
01327         striterator  it = cpy.begin();
01328         for ( ; it != fin ; ++it )
01329                 (*it) = (!(*it));
01330         cpy.aux = pardigs();
01331         cpy.reduce();
01332         return cpy;
01333 }
