01437 nat_num_t div2 () const {
01438         nat_num_t & cthis = (*this);
01439         nat_num_t dvndo_int(cthis.abs());
01440         dvndo_int.reduce();
01441         nat_num_t rem(dvndo_int);
01442         const dig dos = 2;
01443 
01444         nat_num_t dvsor_int(dos);
01445 
01446         const usint cssor = ((B==2)?(1):(0));
01447 
01448         nat_num_t dvndo_fra(dvndo_int.substr(0,cssor));
01449         if (B==2) dvsor_int[0]=1;// division por una potencia de B
01450 
01451         dvndo_int.reduce();
01452         dvndo_fra.reduce();
01453 
01454         const usint longtndo  = dvndo_int.size();
01455         const usint longtndo2 = dvndo_fra.size();
01456         const usint longtsor  = 1;
01457         const usint longsor   = 0;
01458 
01459         // hemos dividido por los ceros del divisor (por una potencia de B)
01460         // tanto divisor como dividendo, obteniendo dvndo_int y dvsor_int como
01461         // cocientes enteros.
01462         div_uno(rem,dvndo_int,dvsor_int,longtndo,longsor);
01463         // ahora hacemos lo mismo con el cociente de las potencias de B del
01464         // dividendo obteniendo dvndo_fra
01465 
01466         /* *
01467                 RemReal = RemObtenido*B^cssor + Dvndo_fra
01468 
01469                 rem = rem.cthis.insert(0,cssor,0);
01470                 rem += dvndo_fra;
01471         * */
01473         // rehacer el resto tomando en cuenta los ceros quitados
01474         // acomodar el formato al signo de la operacion
01475         // determinar el signo de la operacion
01476         return dvndo_int;
01477 }
