01479 dig rem2 () const {
01480         nat_num_t & cthis = (*this);
01481         nat_num_t cpy = cthis.reduce();
01482         cpy.reduce();
01483         if (B%2==1) {
01484                 if (cpy.size()==1)
01485                         return dig::Rem2(cpy[0]);
01486                 else {
01487                         usint paridad = 0;
01488                         for ( usint k=0 ; k < cpy.size() ; ++k )
01489                                 paridad += dig::Dig2UInt(dig::Rem2(cpy[k]));
01490                         paridad %= 2;
01491                         return dig(paridad);
01492                 }
01493         }
01494         else {
01495                 return Rem2<B>(cpy[0]);
01496         }
01497 }
