01561 dig     remBm1() const {
01562         nat_num_t cthis(*this),suma;
01563         cthis.abs();
01564         cthis.reduce();
01565         suma.reduce();
01566         suma.insert(0,1,0);
01567         suma.erase(0,(cthis.size())-1);
01568         do {
01569                 for ( usint i = 0 ; i < cthis.size() ; ++i ) {
01570                         suma += cthis[i];
01571                         suma.reduce();
01572                 }
01573         } while (suma.size()>1);
01574 
01575         if ((suma[0]==0)||(suma[0]==(B-1))) return 0;
01576         else return suma[0];
01577 }
