01601                                                 /****************************/
01602                                                 /*                                                      */
01603                                                 /*              Division Generica       */
01604                                                 /*                                                      */
01605                                                 /****************************/
01606 
01607 
01608 inline void     div_gen_step(const nat_num_t & dvsor, nat_num_t & rem, nat_num_t & coc, usint & ndig) const {
01609         const nat_num_t & cthis = (*this);
01610         const int szsor = dvsor.size();
01611         const int maxpossor = szsor-1;
01612         const int szndo = cthis.size();
01613         const int maxposndo = szndo-1;
01614         const nat_num_t & dvndo = cthis;
01615         const uint szrem = rem.size();
01616         if (ndig==0) ndig = szrem;
01617         if (rem < dvsor) {
01618                 coc.push_back(dig(0));
01619                 coc.reduce();
01620         }
01621         else if (rem == dvsor) {
01622                 coc.push_back(dig(1));
01623                 coc.reduce();
01624                 rem.erase(0,rem.size()-1);
01625                 rem[0]=dig(0);
01626         }
01627         else {
01628                         pardigs dighrem;
01629                         if ( szrem > szsor ) dighrem = pardigs(rem[0],rem[1]) ;
01630                         else dighrem = pardigs(dig(0),rem[0]) ;
01631                         pardigs dighsor        = pardigs(dig(0),dvsor[0]) ;
01632                         pardigs & digcocprueba = dighrem ;
01633                         digcocprueba /= dighsor ;
01634                         if (digcocprueba > pd_Bm1<B>()) {
01635                                 digcocprueba = pd_Bm1<B>();
01636                         }
01637                         nat_num_t remprueba(dvsor) ;
01638                         remprueba *= digcocprueba;
01639                         for ( pardigs ix(0,0)  ; ix < pardigs(1,0) ; ix += pardigs(0,1) ) {
01640                                 if ( remprueba > rem ) {
01641                                         --digcocprueba;
01642                                         remprueba -= dvsor;
01643                                 }
01644                                 else {
01645                                         rem -= remprueba;
01646                                         coc.push_back(digcocprueba.g_uds());//g_first, g_second ??
01647                                         coc.reduce();
01648                                         break;
01649                                 }
01650                         }
01651         }
01652         if(dvndo.size()> ndig) {
01653                 rem.push_back(dvndo[ndig]);
01654         }
01655         rem.reduce();
01656         ++ndig;
01657         return;
01658 }
01659 
01660                                                 /************************************/
01661                                                 /*                                                              */
01662                                                 /*              Un Paso de Division Simple      */
01663                                                 /*                                                              */
01664                                                 /************************************/
01665 public:
01666 inline bool es_cero() const {
01667         bool escero = (size()==0);
01668         if (escero) return true;
01669         escero = (size()==1)and(operator[](0)==dig_t(0));
01670         if (escero) return true;
01671         return false;
01672 }
01673 
01674 inline bool no_es_cero() const {
01675         bool noescero = (size()>1);
01676         if (noescero) return true;
01677         noescero = (size()==1)and(operator[](0)!=dig_t(0));
01678         if (noescero) return true;
01679         return false;
01680 }
01681 private:
01682 inline void div_uno( nat_num_t & rem , nat_num_t & dvndo, nat_num_t & dvsor, const usint szndo, const usint szsor) const {
01683         const nat_num_t & cthis = (*this);
01684         rem = dvndo;
01685         if (dvsor.es_cero()) { // caso de division por 0
01686                 return;
01687         }
01688         else if ((dvndo.es_cero())or(szndo < szsor)) { // caso de 0 dividido por !=0 √≥ caso de cociente 0
01689                 rem = dvndo;
01690                 dvndo.erase(0,(cthis.size())-2);
01691                 dvndo[0] = dig(0);
01692                 return;
01693                 // rem=dvndo; hecho por defecto
01694         }
01695         else if (dvndo==dvsor) { // caso de dividir por si mismo
01696                 dvndo.clear();
01697                 rem.clear();
01698                 dvndo.push_front(dig(1));
01699                 rem.push_front(dig(0));
01700                 return;
01701         }
01702         else if ((szsor==1)&&(dvsor[0]==1)) { // caso de dividir por potencias de B (1)
01703                 //dvndo;
01704                 if (rem.size() > 1){
01705                         rem.erase(0,rem.size()-2);
01706                 }
01707                 rem[0]=dig(0);
01708                 return;
01709         }
01710         else if ((szndo == szsor) && (dvndo < dvsor)) {
01711                 rem = dvndo;
01712                 dvndo.clear();
01713                 dvndo.push_front(dig(0));
01714                 return;
01715         }
01716         else {
01717                 // ((long_dvndo > long_dvsor) or (dvndo > dvsor))
01718                 // algoritmo de resta general
01719                 // implementar div_step pasamos (resto,cociente,i,dividendo,divisor) devuelve (resto,cociente,i,dividendo,divisor)
01720                 // se puede hacer con una tupla
01721                 // dvndo;
01722                 rem   = dvndo;
01723                 rem.reduce();
01724                 rem.erase(szsor,szndo-szsor);
01725                 nat_num_t coc(dig(0));
01726                 usint ndig = 0;
01727                 do {
01728                         div_gen_step(dvsor,rem,coc,ndig);
01729                 } while ( ndig <= szndo );
01730                 dvndo = coc;
01731                 return;
01732         }
01733 }
