01737                         /****************************/
01738                         /*                                                      */
01739                         /*              Division Euclidea       */
01740                         /*                                                      */
01741                         /****************************/
01742 
01743 public:
01744 
01745 pair< nat_num_t , nat_num_t > EuclidDiv (const nat_num_t & arg) const {
01746         const nat_num_t & cthis = (*this);
01749         nat_num_t dvndo_int = cthis;
01750         dvndo_int.reduce();
01751         nat_num_t dvsor_int = arg.abs();
01752         dvsor_int.reduce();
01753 
01754         nat_num_t rem(dvndo_int);
01755 
01756         const usint cssor = dvsor_int.ceros_a_la_drcha(); //¬ø?
01757 
01758         nat_num_t dvndo_fra(dvndo_int.substr(0,cssor));
01759         dvsor_int.erase(0,cssor);// division por una potencia de B
01760 
01761         dvsor_int.reduce();
01762         dvndo_fra.reduce_fracc();// reduce 0s a la derecha
01763 
01764         const usint longtndo  = dvndo_int.size();
01765         const usint longtndo2 = dvndo_fra.size();
01766         const usint longtsor  = dvsor_int.size();
01767         const usint longsor   = longtsor;
01768 
01769         // hemos dividido por los ceros del divisor (por una potencia de B)
01770         // tanto divisor como dividendo, obteniendo dvndo_int y dvsor_int como
01771         // cocientes enteros.
01772         div_uno(rem,dvndo_int,dvsor_int,longtndo,longsor);
01773         // ahora hacemos lo mismo con el cociente de las potencias de B del
01774         // dividendo obteniendo dvndo_fra
01775 
01776         /* *
01777                 RemReal = RemObtenido*B^cssor + Dvndo_fra
01778          * */
01779         rem = rem.insert(0,cssor,0);
01780         rem += dvndo_fra;
01781 
01783         // rehacer el resto tomando en cuenta los ceros quitados
01784         // acomodar el formato al signo de la operacion
01785         // determinar el signo de la operacion
01786         return make_pair(rem,dvndo_int);
01787 }
01788 
01789 pair< nat_num_t , const nat_num_t & > m_EuclidDiv (const nat_num_t & arg) {
01790         const nat_num_t & cthis = (*this);
01793         cthis.absp();
01794         cthis.reduce();
01795         nat_num_t & dvndo_int = cthis;
01796         nat_num_t dvsor_int = arg.abs();
01797         dvsor_int.reduce();
01798 
01799         nat_num_t rem(dvndo_int);
01800 
01801         const usint cssor = dvsor_int.ceros_a_la_drcha(); //¬ø?
01802 
01803         nat_num_t dvndo_fra(dvndo_int.substr(0,cssor));
01804         dvsor_int.erase(0,cssor);// division por una potencia de B
01805 
01806         dvsor_int.reduce();
01807         dvndo_int.reduce();
01808 
01809         dvndo_fra.reduce_fracc();
01810 
01811         const usint longtndo  = dvndo_int.size();
01812         const usint longtndo2 = dvndo_fra.size();
01813         const usint longtsor  = dvsor_int.size();
01814         const usint longsor   = longtsor;
01815 
01816         // hemos dividido por los ceros del divisor (por una potencia de B)
01817         // tanto divisor como dividendo, obteniendo dvndo_int y dvsor_int como
01818         // cocientes enteros.
01819         div_uno(rem,dvndo_int,dvsor_int,longtndo,longsor);
01820         // ahora hacemos lo mismo con el cociente de las potencias de B del
01821         // dividendo obteniendo dvndo_fra
01822 
01823         /* *
01824                 RemReal = RemObtenido*B^cssor + Dvndo_fra
01825          * */
01826         rem = rem.insert(0,cssor,0);
01827         rem += dvndo_fra;
01828 
01830         // rehacer el resto tomando en cuenta los ceros quitados
01831         // acomodar el formato al signo de la operacion
01832         // determinar el signo de la operacion
01833         return make_pair(rem,dvndo_int);
01834 }
01835 
01836                         /************************************/
01837                         /*                                                                                                                                      */
01838                         /*                      PRE Y POST                                                                      */
01839                         /*                                                                                                                                      */
01840                         /************************************/
01841 
01842 public:
01843 
01844 const nat_num_t & operator ++ () {
01845         nat_num_t & cthis = (*this);
01846         cthis += dig(1);
01847         return cthis;
01848 }
01849 
01850 nat_num_t operator ++ (int) {
01851         nat_num_t cpThis(*this);
01852         nat_num_t & cthis = (*this);
01853         cpThis += dig(1);
01854         cthis = cpThis;
01855         return cpThis;
01856 }
01857 
01858 const nat_num_t & operator -- () {
01859         nat_num_t & cthis = (*this);
01860         cthis -= dig(1);
01861         return cthis;
01862 }
01863 
01864 nat_num_t operator -- (int) {
01865         nat_num_t cpThis(*this);
01866         nat_num_t & cthis = (*this);
01867         cpThis -= dig(1);
01868         cthis = cpThis;
01869         return cpThis;
01870 }
01871                                                         /****************************/
01872                                                         /*                                                      */
01873                                                         /*                      REDUCE                  */
01874                                                         /*                                                      */
01875                                                         /****************************/
01876 
01877 inline const nat_num_t & reduce() {
01878         nat_num_t & cthis = (*this);
01879         if (cthis.size()==0) {
01880                 cthis = nat_num_t(dig(0));
01881                 return cthis;
01882         }
01883         else if (cthis.size()==1) return cthis;
01884         else {
01885                 const usint nsz = digs_no_significativos();
01886                 if (nsz==0) return cthis;
01887                 else {
01888                         const usint pos = 0;
01889                         if (nsz>0) cthis.erase(pos,nsz);
01890                         return cthis;
01891                 }
01892         }
01893 }
01894 
01895 inline nat_num_t reduce() const {
01896         const nat_num_t & cthis = (*this);
01897         nat_num_t cpyreducida(cthis);
01898                 if (cpyreducida.size()==0) {
01899                 cpyreducida = nat_num_t(dig(0));
01900                 return cpyreducida;
01901         }
01902         else if (cpyreducida.size()==1) return cpyreducida;
01903         else {
01904                 const usint nsz = cpyreducida.digs_no_significativos();
01905                 if (nsz==0) return cpyreducida;
01906                 else {
01907                         const usint pos = 0;
01908                         if (nsz>0) cpyreducida.erase(pos,nsz);
01909                         return cpyreducida;
01910                 }
01911         }
01912 }
01913 
01914 inline const nat_num_t & reduce_fracc() {
01915         nat_num_t & cthis = (*this);
01916         if (cthis.size()==0) {
01917                 cthis = nat_num_t(dig(0));
01918                 return cthis;
01919         }
01920         else if (cthis.size()==1) return cthis;
01921         else {
01922                 const usint nsz = digs_fracc_no_significativos();
01923                 if (nsz==0) return cthis;
01924                 else {
01925                         const usint pos = 0;
01926                         cthis.erase(pos,nsz);
01927                         return cthis;
01928                 }
01929         }
01930 }
01931 
01932 inline nat_num_t reduce_fracc() const {
01933         const nat_num_t & cthis = (*this);
01934         nat_num_t cpyreducida(cthis);
01935                 if (cpyreducida.size()==0) {
01936                 cpyreducida = nat_num_t(dig(0));
01937                 return cpyreducida;
01938         }
01939         else if (cpyreducida.size()==1) return cpyreducida;
01940         else {
01941                 const usint nsz = cpyreducida.digs_fracc_no_significativos();
01942                 if (nsz==0) return cpyreducida;
01943                 else {
01944                         const usint pos = 0;
01945                         cpyreducida.erase(pos,nsz);
01946                         return cpyreducida;
01947                 }
01948         }
01949 }
01950 
01951 };
