01955                                                 /****************************/
01956                                                 /*                                                      */
01957                                                 /*              ISTREAM Y OSTREAM       */
01958                                                 /*                                                      */
01959                                                 /****************************/
01960 
01961 
01962 
01963 //uint#:dig:dig:#B
01964 template<const uchint Base>
01965 istream & operator >> (istream & is,nat_num_t<Base> & arg) {
01966         enum estado_e {e0,e1,e2,e3,e4,e5,e6,e7,e8,e9,e10,e11,e12,e13};
01967         vector<string> num(255);
01968         string sds;
01969         string num_digint;
01970         string num_base;
01971         usint numero_base_recogido      = 0;
01972         usint ind_sds                           = 0;
01973         usint digint                            = 0;
01974         usint ind_num                           = 0;
01975         estado_e est_act                        = e0;
01976         char c;
01977         is >> sds;
01978         do {
01979                 c = sds[ind_sds];
01980                 switch (est_act) {
01981                 case e0 :
01982                         {
01983                                 num_digint.clear();
01984                                 num_base.clear();
01985                                 numero_base_recogido = 0;
01986                                 digint = 0;
01987                                 ind_num = 0;
01988                         }
01989                         if (c=='u') est_act = e1;
01990                         break;
01991 
01992                 case e1 :
01993                         if (c=='i') est_act = e2;
01994                         else est_act = e0;
01995                         break;
01996 
01997                 case e2 :
01998                         if (c=='n') est_act = e3;
01999                         else est_act = e0;
02000                         break;
02001 
02002                 case e3 :
02003                         if (c=='t') est_act = e4;
02004                         else est_act = e0;
02005                         break;
02006 
02007                 case e4 :
02008                         if (c=='#') est_act = e5;
02009                         else est_act = e0;
02010                         break;
02011 
02012                 case e5 :
02013                         if ((c>='1')and(c<='9')) {
02014                                 num[ind_num].push_back(c-'0');
02015                                 est_act = e6;
02016                         }
02017                         else est_act=e0;
02018                         break;
02019                 case e6 :
02020                         if ((c>='0')and(c<='9')) {
02021                                 num[ind_num].push_back(c-'0');
02022                         }
02023                         else if (c==':') {
02024                                 est_act=e7;
02025                                 ++ind_num;
02026                         }
02027                         else if (c=='#') {
02028                                 est_act=e8;
02029                                 ++ind_num;
02030                         }
02031                         else est_act=e0;
02032                         break;
02033                 case e7 :
02034                         if ((c>='0')and(c<='9')) {
02035                                 est_act = e6;
02036                                 num[ind_num].push_back(c-'0');
02037                         }
02038                         else est_act = e0;
02039                         break;
02040                 case e8 :
02041                         if (c=='B') {
02042                                 est_act = e9;
02043                         }
02044                         else est_act = e0;
02045                         break;
02046                 case e9 :
02047                         if ((c<='9')and(c>='2')){
02048                                 est_act = e10;
02049                                 num_base.push_back(c-'0');
02050                         }
02051                         else if (c=='1') {
02052                                 est_act = e11;
02053                                 num_base.push_back(1);
02054                         }
02055                         else if (c==' ' || c=='\0')
02056                                 est_act = e12;
02057                         else est_act=e0;
02058                         break;
02059                 case e10 :
02060                         if ((c<='9')and(c>='0'))
02061                                 num_base.push_back(c-'0');
02062                         else if(c==' ' || c=='\0')
02063                                 est_act = e12;
02064                         else est_act = e0;
02065                         break;
02066                 case e11 :
02067                         if ((c<='9')and(c>='0')) {
02068                                 est_act=e9;
02069                                 num_base.push_back(c-'0');
02070                         }
02071                         else if(c==' ' || c=='\0')
02072                                 est_act = e12;
02073                         else est_act = e0;
02074                         break;
02075                 case e12 :
02076                         for (uint k=0 ; k < num_base.size() ; ++k ) {
02077                                 numero_base_recogido *= 10;
02078                                 numero_base_recogido += num_base[k];
02079                         }
02080                         if (numero_base_recogido!=Base) est_act = e0;
02081                         else est_act = e13;
02082                 case e13 : break;
02083                 default  : est_act=e0;
02084                 }
02085                 ++ind_sds;
02086         } while ((est_act!=e13)and(c!='\0'));
02087 
02088         arg.clear();
02089         uchint numero;
02090         for (uchint j=0 ; j < ind_num ; ++j) {
02091                 numero=0;
02092                 for (uint k=0 ; k < num[j].size() ; ++k ) {
02093                         numero *= 10;
02094                         numero += num[j][k];
02095                 }
02096                 arg.push_back(dig_t<Base>(numero));
02097         }
02098         arg.p_aux(pardigs_t<Base>());
02099         return is;
02100 }
02101 
02102 template<const uchint Base>
02103 ostream & operator << (ostream & os,const nat_num_t<Base> & arg) {
02104         usint sz = arg.size();
02105         os  << "uint#";
02106         for (int k=0 ; k<sz ; ++k) {
02107                 if (k==0)
02108                         os      << arg[k].Dig2Int();
02109                 else
02110                         os      << ":" << arg[k].Dig2Int();
02111         }
02112         os      <<"#B"
02113                 << static_cast<int>(Base) ;
02114         return os;
02115 }
